// Extraire les données du webhook
const data = $input.first().json.body || $input.first().json;

console.log('Données facture email reçues:', JSON.stringify(data));

const invoiceNumber = data.invoice_number || 'N/A';
const clientName = data.client_name || 'Client';
const total = data.total || 0;
const htmlUrl = data.pdf_url || '';
const email = data.email || '';

return [{
  json: {
    invoiceNumber,
    clientName,
    total,
    htmlUrl,
    email
  }
}];

// Récupérer les données précédentes et le fichier PDF
const previousData = $('Extract Data').first().json;
const binaryData = $input.first().binary;

const invoiceNumber = previousData.invoiceNumber;
const clientName = previousData.clientName;
const total = previousData.total;
const email = previousData.email;

// Formater le montant
const formattedTotal = new Intl.NumberFormat('fr-FR').format(total) + ' FCFA';

// Construire le corps de l'email en HTML
const htmlBody = `
<!DOCTYPE html>
<html>
<head>
  <meta charset="utf-8">
  <style>
    body { font-family: Arial, sans-serif; line-height: 1.6; color: #333; }
    .container { max-width: 600px; margin: 0 auto; padding: 20px; }
    .header { background: #2c3e50; color: white; padding: 20px; text-align: center; }
    .content { padding: 20px; background: #f9f9f9; }
    .invoice-info { background: white; padding: 15px; border-radius: 5px; margin: 15px 0; }
    .total { font-size: 24px; color: #27ae60; font-weight: bold; }
    .footer { text-align: center; padding: 20px; color: #666; font-size: 12px; }
  </style>
</head>
<body>
  <div class="container">
    <div class="header">
      <h1>TECHZONE</h1>
    </div>
    <div class="content">
      <p>Bonjour <strong>${clientName}</strong>,</p>
      <p>Veuillez trouver ci-joint votre facture.</p>

      <div class="invoice-info">
        <p><strong>Numéro de facture :</strong> ${invoiceNumber}</p>
        <p><strong>Montant total :</strong> <span class="total">${formattedTotal}</span></p>
      </div>

      <p style="margin-top: 20px;">Pour toute question, n'hésitez pas à nous contacter.</p>
      <p>Cordialement,<br><strong>L'équipe TECHZONE</strong></p>
    </div>
    <div class="footer">
      <p>TECHZONE - Votre partenaire technologique</p>
    </div>
  </div>
</body>
</html>
`;

return [{
  json: {
    to: email,
    subject: `Facture ${invoiceNumber} - TECHZONE`,
    html: htmlBody,
    attachmentName: `Facture_${invoiceNumber}.pdf`
  },
  binary: binaryData
}];
