// Extraire les données du webhook
const data = $input.first().json.body || $input.first().json;

console.log('Données devis email reçues:', JSON.stringify(data));

const quotationNumber = data.quotation_number || 'N/A';
const clientName = data.client_name || 'Client';
const total = data.total || 0;
const htmlUrl = data.pdf_url || '';
const email = data.email || '';
const validUntil = data.valid_until || '';

return [{
  json: {
    quotationNumber,
    clientName,
    total,
    htmlUrl,
    email,
    validUntil
  }
}];

// Récupérer les données précédentes et le fichier PDF
const previousData = $('Extract Data').first().json;
const binaryData = $input.first().binary;

const quotationNumber = previousData.quotationNumber;
const clientName = previousData.clientName;
const total = previousData.total;
const email = previousData.email;
const validUntil = previousData.validUntil;

// Formater le montant
const formattedTotal = new Intl.NumberFormat('fr-FR').format(total) + ' FCFA';

// Construire le corps de l'email en HTML
const htmlBody = `
<!DOCTYPE html>
<html>
<head>
  <meta charset="utf-8">
  <style>
    body { font-family: Arial, sans-serif; line-height: 1.6; color: #333; }
    .container { max-width: 600px; margin: 0 auto; padding: 20px; }
    .header { background: #2c3e50; color: white; padding: 20px; text-align: center; }
    .content { padding: 20px; background: #f9f9f9; }
    .quote-info { background: white; padding: 15px; border-radius: 5px; margin: 15px 0; }
    .total { font-size: 24px; color: #e67e22; font-weight: bold; }
    .validity { color: #e74c3c; font-weight: bold; }
    .footer { text-align: center; padding: 20px; color: #666; font-size: 12px; }
  </style>
</head>
<body>
  <div class="container">
    <div class="header">
      <h1>GeekTechnologie</h1>
    </div>
    <div class="content">
      <p>Bonjour <strong>${clientName}</strong>,</p>
      <p>Suite à votre demande, veuillez trouver ci-joint notre devis.</p>

      <div class="quote-info">
        <p><strong>Numéro de devis :</strong> ${quotationNumber}</p>
        <p><strong>Montant total :</strong> <span class="total">${formattedTotal}</span></p>
        ${validUntil ? `<p><strong>Valide jusqu'au :</strong> <span class="validity">${validUntil}</span></p>` : ''}
      </div>

      <p style="margin-top: 20px;">N'hésitez pas à nous contacter pour toute question ou pour valider ce devis.</p>
      <p>Cordialement,<br><strong>L'équipe GeekTechnologie</strong></p>
    </div>
    <div class="footer">
      <p>GeekTechnologie - Votre partenaire technologique</p>
    </div>
  </div>
</body>
</html>
`;

return [{
  json: {
    to: email,
    subject: `Devis ${quotationNumber} - GeekTechnologie`,
    html: htmlBody,
    attachmentName: `Devis_${quotationNumber}.pdf`
  },
  binary: binaryData
}];
